import React from 'react';

const TodoTab = ({ tabState, activeTab }) => {
  const tabs = [ 'All', 'Active', 'Completed' ];

  return (
    <ul className="nav">
      { tabs.map( tab => (
        <li
          key={ tab }
          className={ tabState === tab ? "active" : "" }
          onClick={ () => {
            activeTab( tab );
          } }
        >
          { tab }
        </li>
      ) ) }
    </ul>
  );
};

export default TodoTab;
