import React, { useContext } from 'react';
import TodoListItem from "./TodoListItem";
import TodosContext from "../context/TodosContext";

const TodoList = () => {
  const { state, action } = useContext( TodosContext );

  const removeTodo = id => {
    action.setTodos( state.todos.filter( todo => todo.id !== id ) );
  };

  const toggleChk = id => {
    action.setTodos( state.todos.map( todo =>
      todo.id === id ? { ...todo, completed: !todo.completed } : todo ) );
  };

  return (
    <ul className="todos">
      { state.todos
        .filter( todo => {
          if ( state.tabState === "Active" ) return !todo.completed;
          if ( state.tabState === "Completed" ) return todo.completed;
          return true;
        } )
        .map( todo => {
          return (
            <TodoListItem
              key={ todo.id + todo.content }
              todo={ todo }
              removeTodo={ removeTodo }
              toggleChk={ toggleChk }
            />
          );
        } ) }
    </ul>
  );
};

export default TodoList;
